mapper.update:
UPDATE COMMU_KEY_SET
MAC_NO = #{macNo,jdbcType=VARCHAR}, KEY_TYPE = #{keyType,jdbcType=VARCHAR}, IS_YIELD = #{isYield,jdbcType=INTEGER}, DIRECTION = #{direction,jdbcType=INTEGER}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, DELETED = #{deleted,jdbcType=VARCHAR}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE COMMU_KEY_SET
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

FROM COMMU_KEY_SET
WHERE ID = #{id,jdbcType=INTEGER}
AND DELETED = #{deleted,jdbcType=VARCHAR}


FROM COMMU_KEY_SET


        AND ID = #{id,jdbcType=INTEGER}


        AND MAC_NO = #{macNo,jdbcType=VARCHAR}


        AND KEY_TYPE = #{keyType,jdbcType=VARCHAR}


        AND IS_YIELD = #{isYield,jdbcType=INTEGER}


        AND DIRECTION = #{direction,jdbcType=INTEGER}


        AND DELETED = #{deleted,jdbcType=VARCHAR}


        AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


FROM COMMU_KEY_SET


        AND ID = #{id,jdbcType=INTEGER}


        AND MAC_NO = #{macNo,jdbcType=VARCHAR}


        AND KEY_TYPE = #{keyType,jdbcType=VARCHAR}


        AND IS_YIELD = #{isYield,jdbcType=INTEGER}


        AND DIRECTION = #{direction,jdbcType=INTEGER}


        AND DELETED = #{deleted,jdbcType=VARCHAR}


        AND COMPANY_ID = #{companyId,jdbcType=BIGINT}

    WHERE DELETED = "F"
