mapper.update:
UPDATE COMMU_UP_DATA_PMS
CAT_NO = #{catNo,jdbcType=VARCHAR}, MAC_NO = #{macNo,jdbcType=VARCHAR}, COMMUNICATION_KEY = #{communicationKey,jdbcType=VARCHAR}, COMMUNICATION_VALUE = #{communicationValue,jdbcType=DOUBLE}, COMMUNICATION_TIME = #{communicationTime,jdbcType=TIMESTAMP}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, DELETED = #{deleted,jdbcType=TINYINT}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE COMMU_UP_DATA_PMS
SET DELETED     = #{deleted,jdbcType=VARCHAR},
    MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
    MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

FROM COMMU_UP_DATA_PMS
WHERE ID = #{id,jdbcType=INTEGER}
AND DELETED = #{deleted,jdbcType=VARCHAR}


FROM COMMU_UP_DATA_PMS


        AND ID = #{id,jdbcType=INTEGER}


        AND CAT_NO = #{catNo,jdbcType=VARCHAR}


        AND MAC_NO = #{macNo,jdbcType=VARCHAR}


        AND COMMUNICATION_KEY = #{communicationKey,jdbcType=VARCHAR}


        AND COMMUNICATION_VALUE = #{communicationValue,jdbcType=DOUBLE}


        AND COMMUNICATION_TIME = #{communicationTime,jdbcType=TIMESTAMP}


        AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


        AND DELETED = #{deleted,jdbcType=TINYINT}


FROM COMMU_UP_DATA_PMS


        AND ID = #{id,jdbcType=INTEGER}


        AND CAT_NO = #{catNo,jdbcType=VARCHAR}


        AND MAC_NO = #{macNo,jdbcType=VARCHAR}


        AND COMMUNICATION_KEY = #{communicationKey,jdbcType=VARCHAR}


        AND COMMUNICATION_VALUE = #{communicationValue,jdbcType=DOUBLE}


        AND COMMUNICATION_TIME = #{communicationTime,jdbcType=TIMESTAMP}


        AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


        AND DELETED = #{deleted,jdbcType=TINYINT}

    WHERE DELETED = "F"
