mapper.update:
UPDATE SIT_SET
ID = #{id,jdbcType=BIGINT}, MAC_NO = #{macNo,jdbcType=VARCHAR}, SITE_NAME = #{siteName,jdbcType=VARCHAR}, Z = #{z,jdbcType=DOUBLE}, Y = #{y,jdbcType=DOUBLE}, X = #{x,jdbcType=DOUBLE}, OFFSET = #{offset,jdbcType=DOUBLE}, SITE_TYPE = #{siteType,jdbcType=INTEGER}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, DELETED = #{deleted,jdbcType=TINYINT}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE SIT_SET
SET DELETED     = #{deleted,jdbcType=VARCHAR},
    MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
    MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

FROM SIT_SET
WHERE ID = #{id,jdbcType=INTEGER}
AND DELETED = #{deleted,jdbcType=VARCHAR}


FROM SIT_SET


        AND ID = #{id,jdbcType=INTEGER}


        AND ID = #{id,jdbcType=BIGINT}


        AND MAC_NO = #{macNo,jdbcType=VARCHAR}


        AND SITE_NAME = #{siteName,jdbcType=VARCHAR}


        AND Z = #{z,jdbcType=DOUBLE}


        AND Y = #{y,jdbcType=DOUBLE}


        AND X = #{x,jdbcType=DOUBLE}


        AND OFFSET = #{offset,jdbcType=DOUBLE}


        AND SITE_TYPE = #{siteType,jdbcType=INTEGER}


        AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


        AND DELETED = #{deleted,jdbcType=TINYINT}


FROM SIT_SET


        AND ID = #{id,jdbcType=INTEGER}


        AND ID = #{id,jdbcType=BIGINT}


        AND MAC_NO = #{macNo,jdbcType=VARCHAR}


        AND SITE_NAME = #{siteName,jdbcType=VARCHAR}


        AND Z = #{z,jdbcType=DOUBLE}


        AND Y = #{y,jdbcType=DOUBLE}


        AND X = #{x,jdbcType=DOUBLE}


        AND OFFSET = #{offset,jdbcType=DOUBLE}


        AND SITE_TYPE = #{siteType,jdbcType=INTEGER}


        AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


        AND DELETED = #{deleted,jdbcType=TINYINT}

    WHERE DELETED = "F"
