mapper.update:
UPDATE SYSTEM_TASK
AREA_ID = #{areaId,jdbcType=BIGINT}, STORE_ID = #{storeId,jdbcType=BIGINT}, START_SITE_ID = #{startSiteId,jdbcType=BIGINT}, END_SITE_ID = #{endSiteId,jdbcType=BIGINT}, IN_MARK = #{inMark,jdbcType=INTEGER}, AMOUNT = #{amount,jdbcType=INTEGER}, REQUEST_TIME = #{requestTime,jdbcType=TIMESTAMP}, TASK_ID = #{taskId,jdbcType=BIGINT}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, DELETED = #{deleted,jdbcType=TINYINT}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE SYSTEM_TASK
SET DELETED     = #{deleted,jdbcType=VARCHAR},
    MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
    MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

FROM SYSTEM_TASK
WHERE ID = #{id,jdbcType=INTEGER}
AND DELETED = #{deleted,jdbcType=VARCHAR}


FROM SYSTEM_TASK


        AND ID = #{id,jdbcType=INTEGER}


        AND AREA_ID = #{areaId,jdbcType=BIGINT}


        AND STORE_ID = #{storeId,jdbcType=BIGINT}


        AND START_SITE_ID = #{startSiteId,jdbcType=BIGINT}


        AND END_SITE_ID = #{endSiteId,jdbcType=BIGINT}


        AND IN_MARK = #{inMark,jdbcType=INTEGER}


        AND AMOUNT = #{amount,jdbcType=INTEGER}


        AND REQUEST_TIME = #{requestTime,jdbcType=TIMESTAMP}


        AND TASK_ID = #{taskId,jdbcType=BIGINT}


        AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


        AND DELETED = #{deleted,jdbcType=TINYINT}


FROM SYSTEM_TASK


        AND ID = #{id,jdbcType=INTEGER}


        AND AREA_ID = #{areaId,jdbcType=BIGINT}


        AND STORE_ID = #{storeId,jdbcType=BIGINT}


        AND START_SITE_ID = #{startSiteId,jdbcType=BIGINT}


        AND END_SITE_ID = #{endSiteId,jdbcType=BIGINT}


        AND IN_MARK = #{inMark,jdbcType=INTEGER}


        AND AMOUNT = #{amount,jdbcType=INTEGER}


        AND REQUEST_TIME = #{requestTime,jdbcType=TIMESTAMP}


        AND TASK_ID = #{taskId,jdbcType=BIGINT}


        AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


        AND DELETED = #{deleted,jdbcType=TINYINT}

    WHERE DELETED = "F"
